    def configConfirm(self):
        self.collectConfigration()
        if self.heart == '' or self.spade == '' or self.club == '' or self.diamond == '' or self.bonus == '':
            QtWidgets.QMessageBox.information(self, '提示', '好像还没有填完呢')
        else:
            self.getBcardState()
            self.goAthleteConfirm()

    def collectConfigration(self):
        self.heart = self.lineEditWodHeart.text()
        self.spade = self.lineEditWodSpade.text()
        self.club = self.lineEditWodClub.text()
        self.diamond = self.lineEditWodDiamond.text()
        self.bonus = self.lineEditWodBonus.text()
        self.suit = [self.heart, self.spade, self.club, self.diamond, self.bonus]
        self.actionConfig.update(
            {'heart': self.heart, 'spade': self.spade, 'club': self.club, 'diamond': self.diamond,
             'bonus': self.bonus})

    def goAthleteConfirm(self):
        self.switch_window.emit(self.actionConfig, self.BcardList)

    def getBcardState(self):
        """
        获取Bcard中勾选的选项
        :return: Bcard中勾选的选项
        """
        stateA = self.checkBoxA.checkState()
        state2 = self.checkBox2.checkState()
        state3 = self.checkBox3.checkState()
        state4 = self.checkBox4.checkState()
        state5 = self.checkBox5.checkState()
        state6 = self.checkBox6.checkState()
        state7 = self.checkBox7.checkState()
        state8 = self.checkBox8.checkState()
        state9 = self.checkBox9.checkState()
        state10 = self.checkBox10.checkState()
        stateJ = self.checkBoxJ.checkState()
        stateQ = self.checkBoxQ.checkState()
        stateK = self.checkBoxK.checkState()
        Bcard = [stateA, state2, state3, state4, state5, state6, state7, state8, state9, state10, stateJ, stateQ,
                 stateK]
        for index in range(len(Bcard)):
            if Bcard[index] == 2:
                if index == 0:
                    self.BcardList.append('A')
                elif index == 10:
                    self.BcardList.append('J')
                elif index == 11:
                    self.BcardList.append('Q')
                elif index == 12:
                    self.BcardList.append('K')
                else:
                    self.BcardList.append(str(index + 1))